                $('#StatusModal').modal('show');
                break;
            case ""UpdateInfoSuccess"":
                $('#StatusMessage').text(""CẬP NHẬT THÔNG TIN THÀNH CÔNG"")
                $('#StatusModal').modal('show');
                break;
            case ""UpdateInfoFaild"":
                $('#StatusMessage').text(""CẬP NHẬT THÔNG TIN THẤT THẤT BẠI"")
                $('#StatusModal').modal('show');
                break;
        }
    });
    function ValidateUpdateInfo() {
